package org.miller.literalura.Api;import java.io.IOException;import java.net.URI;import java.net.http.HttpClient;import java.net.http.HttpRequest;import java.net.http.HttpResponse;public class Api {    private static final String BASE_URL = "https://gutendex.com/books";    public static String getDatos(String titulo){        HttpClient client = HttpClient.newHttpClient();        HttpRequest request = HttpRequest.newBuilder()                .uri(URI.create(BASE_URL + "/?search=" + titulo.replace(" ", "+")))                .build();        HttpResponse<String> response = null;        try {            response = client.send(request, HttpResponse.BodyHandlers.ofString());        } catch (IOException | InterruptedException e) {            throw new RuntimeException(e);        }        return response.body();    }}