package org.miller.literalura.Service;import org.miller.literalura.Model.Autor;import org.miller.literalura.Respository.AutorRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class AutorService {    public AutorRepository autorRepository;    @Autowired    public AutorService(AutorRepository autorRepository) {        this.autorRepository = autorRepository;    }    public AutorService() {    }    public Optional<Autor> getAutorByName(String nombre) {        return autorRepository.getAutorByName(nombre);    }    public void saveAutor(Autor autor) {        autorRepository.save(autor);    }    public List<Autor> getAllAutores() {        return autorRepository.findAll();    }    public List<Autor> getAutorByAnio(Integer anio) {        return autorRepository.getAutorByAnio(anio);    }}